// src/components/ProfilePage.js
import React, { useState } from 'react';
import  { useEffect }  from 'react';
import './ProfilePage.css'; // Add styles as needed
import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000/api',
  timeout: 5000,
});

const ProfilePage = () => {
  const token = localStorage.getItem("loginToken");
  const [thisUser, setThisUser] = useState();

  const getUserData = async () => {
    try {
      const authStr = "Bearer ".concat(token);
      const response = await api.get('/getuser', {headers: {Authorization: authStr}});
      setThisUser(response.data);
    } catch (err) {
      console.log("error trying to get user data");
    }
  };

  useEffect(() => {
    const fetchData = async () => {
      await getUserData();
    }
    fetchData();
  }, []);

  return (
    <div>
      <div className="profile-page">
        <p>{ thisUser.name }</p>
        <p>{ thisUser.email }</p>
        <h1>Username's events:</h1>
        <p> Sto bi dobro bilo da ovde neko nesto napravi</p>
      </div>
    </div>
  );
};

export default ProfilePage;
