        navigate('/feed');
      } catch (err) {
        console.error('New post error:', err);
        alert('New post error');
      }
    } catch (err) {
      alert('Error adding post'); // Show an error message
    }
  };

  // use addPost to add them to the posts list
  // don't forget to remove addPost from PostForm.js

  return (
    <div className="feed">
      {posts.length > 0 ? (
        posts.map((post) => (
          <div key={post.id} className="post-item">
            <Link to={`/post/${post.id}`} className="post-link">
              <Post
                title={post.title}
                date={post.date}
                description={post.description}
                photo={post.photo} // Ensure this is a URL or base64 string
                participants={post.participants}
              />
            </Link>
          </div>
        ))
      ) : (
        <p>No posts available.</p>
      )}
      <Link to="/post-form" className="button">+</Link>
    </div>
  );
};

export default Feed;
