import React, { createContext, useState, useContext } from 'react';
import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:5000/api',
  timeout: 5000,
});

const PostContext = createContext({
  posts: [],
  addPost: () => {}
});

export const PostProvider = ({ children }) => {
  const [posts, setPosts] = useState([]);

  const sendPost = async (post, e) => {
    e.preventDefault();
    try {
      const response = await api.post('/newpost', post);
      console.log('New post response:', response.data);
      alert(response.data.msg);
    } catch (err) {
      console.error('New post error:', err);
      alert('New post error');
    }
  };

  const addPost = (post) => {
    setPosts((prevPosts) => [...prevPosts, post]);
    // Ovde mozda moze da se doda čuvanje posta u bazu
    sendPost(post);
  };

  return (
    <PostContext.Provider value={{ posts, addPost }}>
      {children}
    </PostContext.Provider>
  );
};

export const usePosts = () => {
  return useContext(PostContext);
};
